INSERT INTO question (code, subsection_id, content, figure) VALUES
  ('E0A01', 1, '{"id": "E0A01", "correct": 1, "refs": "", "question": "What is the primary function of an external earth connection or ground rod?", "answers": ["Prevent static build up on power lines", "Lightning charge dissipation", "Reduce RF current flow between pieces of equipment", "Protect breaker panel from power surges"]}', NULL),
  ('E0A02', 1, '{"id": "E0A02", "correct": 1, "refs": "", "question": "When evaluating RF exposure levels from your station at a neighbor\u2019s home, what must you do?", "answers": ["Ensure signals from your station are less than the controlled maximum permissible exposure (MPE) limits", "Ensure signals from your station are less than the uncontrolled maximum permissible exposure (MPE) limits", "Ensure signals from your station are less than the controlled maximum permissible emission (MPE) limits", "Ensure signals from your station are less than the uncontrolled maximum permissible emission (MPE) limits"]}', NULL),
  ('E0A03', 1, '{"id": "E0A03", "correct": 2, "refs": "", "question": "Over what range of frequencies are the FCC human body RF exposure limits most restrictive?", "answers": ["300 kHz - 3 MHz", "3 - 30 MHz", "30 - 300 MHz", "300 - 3000 MHz"]}', NULL),
  ('E0A04', 1, '{"id": "E0A04", "correct": 2, "refs": "", "question": "When evaluating a site with multiple transmitters operating at the same time, the operators and licensees of which transmitters are responsible for mitigating over-exposure situations?", "answers": ["Each transmitter that produces 20 percent or more of its MPE limit in areas where the total MPE limit is exceeded", "Each transmitter operating with a duty cycle greater than 25 percent", "Each transmitter that produces 5 percent or more of its MPE limit in areas where the total MPE limit is exceeded", "Each transmitter operating with a duty cycle greater than 50 percent"]}', NULL),
  ('E0A05', 1, '{"id": "E0A05", "correct": 1, "refs": "", "question": "What hazard is created by operating at microwave frequencies?", "answers": ["Microwaves are ionizing radiation", "The high gain antennas commonly used can result in high exposure levels", "Microwaves are in the frequency range where wave velocity is higher", "The extremely high frequency energy can damage the joints of antenna structures"]}', NULL),
  ('E0A06', 1, '{"id": "E0A06", "correct": 3, "refs": "", "question": "Why are there separate electric (E) and magnetic (H) MPE limits at frequencies below 300 MHz?", "answers": ["The body reacts to electromagnetic radiation from both the E and H fields", "Ground reflections and scattering cause the field strength to vary with location", "E field and H field radiation intensity peaks can occur at different locations", "All these choices are correct"]}', NULL),
  ('E0A07', 1, '{"id": "E0A07", "correct": 1, "refs": "", "question": "What is meant by \u201c100% tie-off\u201d regarding tower safety?", "answers": ["All loose ropes and guys secured to a fixed structure", "At least one lanyard attached to the tower at all times", "All tools secured to the climber\u2019s harness", "All circuit breakers feeding power to the tower must be tied closed with tape, cable, or ties"]}', NULL),
  ('E0A08', 1, '{"id": "E0A08", "correct": 2, "refs": "", "question": "What does SAR measure?", "answers": ["Signal attenuation ratio", "Signal amplification rating", "The rate at which RF energy is absorbed by the body", "The rate of RF energy reflected from stationary terrain"]}', NULL),
  ('E0A09', 1, '{"id": "E0A09", "correct": 2, "refs": "", "question": "Which of the following types of equipment are exempt from RF exposure evaluations?", "answers": ["Transceivers with less than 7 watts of RF output", "Antennas that radiate only in the near field", "Hand-held transceivers sold before May 3, 2021", "Dish antennas less than one meter in diameter"]}', NULL),
  ('E0A10', 1, '{"id": "E0A10", "correct": 0, "refs": "", "question": "When must an RF exposure evaluation be performed on an amateur station operating on 80 meters?", "answers": ["An evaluation must always be performed", "When the ERP of the station is less than 10 watts", "When the station\u2019s operating mode is CW", "When the output power from the transmitter is less than 100 watts"]}', NULL),
  ('E0A11', 1, '{"id": "E0A11", "correct": 3, "refs": "", "question": "To what should lanyards be attached while climbing?", "answers": ["Antenna mast", "Guy brackets", "Tower rungs", "Tower legs"]}', NULL),
  ('E0A12', 1, '{"id": "E0A12", "correct": 0, "refs": "", "question": "Where should a shock-absorbing lanyard be attached to a tower when working above ground?", "answers": ["Above the climber\u2019s head level", "To the belt of the fall-arrest harness", "Even with the climber''s waist", "To the next lowest set of guys"]}', NULL),
  ('E1A01', 2, '{"id": "E1A01", "correct": 3, "refs": "[97.305, 97.307(b)]", "question": "Why is it not legal to transmit a 3 kHz bandwidth USB signal with a carrier frequency of 14.348 MHz?", "answers": ["USB is not used on 20-meter phone", "The lower 1 kHz of the signal is outside the 20-meter band", "14.348 MHz is outside the 20-meter band", "The upper 1 kHz of the signal is outside the 20-meter band"]}', NULL),
  ('E1A02', 2, '{"id": "E1A02", "correct": 3, "refs": "[97.301, 97.305]", "question": "When using a transceiver that displays the carrier frequency of phone signals, which of the following displayed frequencies represents the lowest frequency at which a properly adjusted LSB emission will be totally within the band?", "answers": ["The exact lower band edge", "300 Hz above the lower band edge", "1 kHz above the lower band edge", "3 kHz above the lower band edge"]}', NULL),
  ('E1A03', 2, '{"id": "E1A03", "correct": 2, "refs": "[97.305, 97.307(b)]", "question": "What is the highest legal carrier frequency on the 20-meter band for transmitting a 2.8 kHz wide USB data signal?", "answers": ["14.0708 MHz", "14.1002 MHz", "14.1472 MHz", "14.3490 MHz"]}', NULL),
  ('E1A04', 2, '{"id": "E1A04", "correct": 2, "refs": "[97.301, 97.305]", "question": "May an Extra class operator answer the CQ of a station on 3.601 MHz LSB phone?", "answers": ["Yes, the entire signal will be inside the SSB allocation for Extra class operators", "Yes, the displayed frequency is within the 75-meter phone band segment", "No, the sideband components will extend beyond the edge of the phone band segment", "No, US stations are not permitted to use phone emissions below 3.610 MHz"]}', NULL),
  ('E1A05', 2, '{"id": "E1A05", "correct": 2, "refs": "[97.5]", "question": "Who must be in physical control of the station apparatus of an amateur station aboard any vessel or craft that is documented or registered in the United States?", "answers": ["Only a person with an FCC Marine Radio license grant", "Only a person named in an amateur station license grant", "Any person holding an FCC issued amateur license or who is authorized for alien reciprocal operation", "Any person named in an amateur station license grant or a person holding an unrestricted Radiotelephone Operator Permit"]}', NULL),
  ('E1A06', 2, '{"id": "E1A06", "correct": 1, "refs": "[97.303(h)(1)]", "question": "What is the required transmit frequency of a CW signal for channelized 60 meter operation?", "answers": ["At the lowest frequency of the channel", "At the center frequency of the channel", "At the highest frequency of the channel", "On any frequency where the signal\u2019s sidebands are within the channel"]}', NULL),
  ('E1A07', 2, '{"id": "E1A07", "correct": 2, "refs": "[97.313(k)]", "question": "What is the maximum power permitted on the 2200-meter band?", "answers": ["50 watts PEP (peak envelope power)", "100 watts PEP (peak envelope power)", "1 watt EIRP (equivalent isotropic radiated power)", "5 watts EIRP (equivalent isotropic radiated power)"]}', NULL),
  ('E1A08', 2, '{"id": "E1A08", "correct": 1, "refs": "[97.219]", "question": "If a station in a message forwarding system inadvertently forwards a message that is in violation of FCC rules, who is primarily accountable for the rules violation?", "answers": ["The control operator of the packet bulletin board station", "The control operator of the originating station", "The control operators of all the stations in the system", "The control operators of all the stations in the system not authenticating the source from which they accept communications"]}', NULL),
  ('E1A09', 2, '{"id": "E1A09", "correct": 3, "refs": "[97.313(l)]", "question": "Except in some parts of Alaska, what is the maximum power permitted on the 630-meter band?", "answers": ["50 watts PEP (peak envelope power)", "100 watts PEP (peak envelope power)", "1 watt EIRP (equivalent isotropic radiated power)", "5 watts EIRP (equivalent isotropic radiated power)"]}', NULL),
  ('E1A10', 2, '{"id": "E1A10", "correct": 0, "refs": "[97.11]", "question": "If an amateur station is installed aboard a ship or aircraft, what condition must be met before the station is operated?", "answers": ["Its operation must be approved by the master of the ship or the pilot in command of the aircraft", "The amateur station operator must agree not to transmit when the main radio of the ship or aircraft is in use", "The amateur station must have a power supply that is completely independent of the main ship or aircraft power supply", "The amateur station must operate only in specific segments of the amateur service HF and VHF bands"]}', NULL),
  ('E1A11', 2, '{"id": "E1A11", "correct": 1, "refs": "[97.5]", "question": "What licensing is required when operating an amateur station aboard a US-registered vessel in international waters?", "answers": ["Any amateur license with an FCC Marine or Aircraft endorsement", "Any FCC-issued amateur license", "Only General class or higher amateur licenses", "An unrestricted Radiotelephone Operator Permit"]}', NULL),
  ('E1B01', 3, '{"id": "E1B01", "correct": 3, "refs": "[97.3]", "question": "Which of the following constitutes a spurious emission?", "answers": ["An amateur station transmission made without the proper call sign identification", "A signal transmitted to prevent its detection by any station other than the intended recipient", "Any transmitted signal that unintentionally interferes with another licensed radio station and whose levels exceed 40 dB below the fundamental power level", "An emission outside the signal\u2019s necessary bandwidth that can be reduced or eliminated without affecting the information transmitted"]}', NULL),
  ('E1B02', 3, '{"id": "E1B02", "correct": 0, "refs": "[97.307(f)(2)]", "question": "Which of the following is an acceptable bandwidth for digital voice or slow-scan TV transmissions made on the HF amateur bands?", "answers": ["3 kHz", "10 kHz", "15 kHz", "20 kHz"]}', NULL),
  ('E1B03', 3, '{"id": "E1B03", "correct": 0, "refs": "[97.13]", "question": "Within what distance must an amateur station protect an FCC monitoring facility from harmful interference?", "answers": ["1 mile", "3 miles", "10 miles", "30 miles"]}', NULL),
  ('E1B04', 3, '{"id": "E1B04", "correct": 2, "refs": "[97.303(b)]", "question": "What must the control operator of a repeater operating in the 70-centimeter band do if a radiolocation system experiences interference from that repeater?", "answers": ["Reduce the repeater antenna HAAT (Height Above Average Terrain)", "File an FAA NOTAM (Notice to Air Missions) with the repeater system''s ERP, call sign, and six-character grid locator", "Cease operation or make changes to the repeater that mitigate the interference", "All these choices are correct"]}', NULL),
  ('E1B05', 3, '{"id": "E1B05", "correct": 2, "refs": "[97.3]", "question": "What is the National Radio Quiet Zone?", "answers": ["An area surrounding the FCC monitoring station in Laurel, Maryland", "An area in New Mexico surrounding the White Sands Test Area", "An area surrounding the National Radio Astronomy Observatory", "An area in Florida surrounding Cape Canaveral"]}', NULL),
  ('E1B06', 3, '{"id": "E1B06", "correct": 0, "refs": "[97.15]", "question": "Which of the following additional rules apply if you are erecting an amateur station antenna structure at a site at or near a public use airport?", "answers": ["You may have to notify the Federal Aviation Administration and register it with the FCC as required by Part 17 of the FCC rules", "You may have to enter the height above ground in meters, and the latitude and longitude in degrees, minutes, and seconds on the FAA website", "You must file an Environmental Impact Statement with the EPA before construction begins", "You must obtain a construction permit from the airport zoning authority per Part 119 of the FAA regulations"]}', NULL),
  ('E1B07', 3, '{"id": "E1B07", "correct": 2, "refs": "[97.15]", "question": "To what type of regulations does PRB-1 apply?", "answers": ["Homeowners associations", "FAA tower height limits", "State and local zoning", "Use of wireless devices in vehicles"]}', NULL),
  ('E1B08', 3, '{"id": "E1B08", "correct": 3, "refs": "[97.121]", "question": "What limitations may the FCC place on an amateur station if its signal causes interference to domestic broadcast reception, assuming that the receivers involved are of good engineering design?", "answers": ["The amateur station must cease operation", "The amateur station must cease operation on all frequencies below 30 MHz", "The amateur station must cease operation on all frequencies above 30 MHz", "The amateur station must avoid transmitting during certain hours on frequencies that cause the interference"]}', NULL),
  ('E1B09', 3, '{"id": "E1B09", "correct": 2, "refs": "[97.407]", "question": "Which amateur stations may be operated under RACES rules?", "answers": ["Only those club stations licensed to Amateur Extra class operators", "Any FCC-licensed amateur station except a Technician class", "Any FCC-licensed amateur station certified by the responsible civil defense organization for the area served", "Only stations meeting the FCC Part 97 technical standards for operation during an emergency"]}', NULL),
  ('E1B10', 3, '{"id": "E1B10", "correct": 0, "refs": "[97.407]", "question": "What frequencies are authorized to an amateur station operating under RACES rules?", "answers": ["All amateur service frequencies authorized to the control operator", "Specific segments in the amateur service MF, HF, VHF, and UHF bands", "Specific local government channels", "All these choices are correct"]}', NULL),
  ('E1B11', 3, '{"id": "E1B11", "correct": 1, "refs": "[97.15]", "question": "What does PRB-1 require of state and local regulations affecting amateur radio antenna size and structures?", "answers": ["No limitations may be placed on antenna size or placement", "Reasonable accommodations of amateur radio must be made", "Such structures must be permitted when use for emergency communications can be demonstrated", "Such structures must be permitted if certified by a registered professional engineer"]}', NULL),
  ('E1C01', 4, '{"id": "E1C01", "correct": 3, "refs": "[97.303]", "question": "What is the maximum bandwidth for a data emission on 60 meters?", "answers": ["60 Hz", "170 Hz", "1.5 kHz", "2.8 kHz"]}', NULL),
  ('E1C02', 4, '{"id": "E1C02", "correct": 2, "refs": "[97.117]", "question": "Which of the following apply to communications transmitted to amateur stations in foreign countries?", "answers": ["Third party traffic must be limited to that intended for the exclusive use of government and non-Government Organization (NGOs) involved in emergency relief activities", "All transmissions must be in English", "Communications must be limited to those incidental to the purpose of the amateur service and remarks of a personal nature", "All these choices are correct"]}', NULL),
  ('E1C03', 4, '{"id": "E1C03", "correct": 1, "refs": "[97.303(g)]", "question": "How long must an operator wait after filing a notification with the Utilities Technology Council (UTC) before operating on the 2200-meter or 630-meter band?", "answers": ["Operators must not operate until approval is received", "Operators may operate after 30 days, providing they have not been told that their station is within 1 kilometer of PLC systems using those frequencies", "Operators may not operate until a test signal has been transmitted in coordination with the local power company", "Operations may commence immediately, and may continue unless interference is reported by the UTC"]}', NULL),
  ('E1C04', 4, '{"id": "E1C04", "correct": 0, "refs": "", "question": "What is an IARP?", "answers": ["A permit that allows US amateurs to operate in certain countries of the Americas", "The internal amateur radio practices policy of the FCC", "An indication of increased antenna reflected power", "A forecast of intermittent aurora radio propagation"]}', NULL),
  ('E1C05', 4, '{"id": "E1C05", "correct": 1, "refs": "[97.221(c)(1), 97.115(c)]", "question": "Under what situation may a station transmit third party communications while being automatically controlled?", "answers": ["Never", "Only when transmitting RTTY or data emissions", "Only when transmitting SSB or CW", "On any mode approved by the National Telecommunication and Information Administration"]}', NULL),
  ('E1C06', 4, '{"id": "E1C06", "correct": 2, "refs": "", "question": "Which of the following is required in order to operate in accordance with CEPT rules in foreign countries where permitted?", "answers": ["You must identify in the official language of the country in which you are operating", "The US embassy must approve of your operation", "You must have a copy of FCC Public Notice DA 16-1048", "You must append \"/CEPT\" to your call sign"]}', NULL),
  ('E1C07', 4, '{"id": "E1C07", "correct": 3, "refs": "[97.303(g)]", "question": "What notifications must be given before transmitting on the 630- or 2200-meter bands?", "answers": ["A special endorsement must be requested from the FCC", "An environmental impact statement must be filed with the Department of the Interior", "Operators must inform the FAA of their intent to operate, giving their call sign and distance to the nearest runway", "Operators must inform the Utilities Technology Council (UTC) of their call sign and coordinates of the station"]}', NULL),
  ('E1C08', 4, '{"id": "E1C08", "correct": 1, "refs": "[97.213]", "question": "What is the maximum permissible duration of a remotely controlled station\u2019s transmissions if its control link malfunctions?", "answers": ["30 seconds", "3 minutes", "5 minutes", "10 minutes"]}', NULL),
  ('E1C09', 4, '{"id": "E1C09", "correct": 1, "refs": "[97.307]", "question": "What is the highest modulation index permitted at the highest modulation frequency for angle modulation below 29.0 MHz?", "answers": ["0.5", "1.0", "2.0", "3.0"]}', NULL),
  ('E1C10', 4, '{"id": "E1C10", "correct": 0, "refs": "[97.307]", "question": "What is the maximum mean power level for a spurious emission below 30 MHz with respect to the fundamental emission?", "answers": ["- 43 dB", "- 53 dB", "- 63 dB", "- 73 dB"]}', NULL),
  ('E1C11', 4, '{"id": "E1C11", "correct": 0, "refs": "[97.5]", "question": "Which of the following operating arrangements allows an FCC-licensed US citizen to operate in many European countries, and amateurs from many European countries to operate in the US?", "answers": ["CEPT", "IARP", "ITU reciprocal license", "All these choices are correct"]}', NULL),
  ('E1C12', 4, '{"id": "E1C12", "correct": 3, "refs": "[97.305(c)]", "question": "In what portion of the 630-meter band are phone emissions permitted?", "answers": ["None", "Only the top 3 kHz", "Only the bottom 3 kHz", "The entire band"]}', NULL),
  ('E1D01', 5, '{"id": "E1D01", "correct": 0, "refs": "[97.3]", "question": "What is the definition of telemetry?", "answers": ["One-way transmission of measurements at a distance from the measuring instrument", "Two-way transmissions in excess of 1000 feet", "Two-way transmissions of data", "One-way transmission that initiates, modifies, or terminates the functions of a device at a distance"]}', NULL),
  ('E1D02', 5, '{"id": "E1D02", "correct": 1, "refs": "[97.211(b)]", "question": "Which of the following may transmit encrypted messages?", "answers": ["Telecommand signals to terrestrial repeaters", "Telecommand signals from a space telecommand station", "Auxiliary relay links carrying repeater audio", "Mesh network backbone nodes"]}', NULL),
  ('E1D03', 5, '{"id": "E1D03", "correct": 1, "refs": "[97.3(a)(45)]", "question": "What is a space telecommand station?", "answers": ["An amateur station located on the surface of the Earth for communication with other Earth stations by means of Earth satellites", "An amateur station that transmits communications to initiate, modify, or terminate functions of a space station", "An amateur station located in a satellite or a balloon more than 50 kilometers above the surface of the Earth", "An amateur station that receives telemetry from a satellite or balloon more than 50 kilometers above the surface of the Earth"]}', NULL),
  ('E1D04', 5, '{"id": "E1D04", "correct": 0, "refs": "[97.119(a)]", "question": "Which of the following is required in the identification transmissions from a balloon-borne telemetry station?", "answers": ["Call sign", "The output power of the balloon transmitter", "The station''s six-character Maidenhead grid locator", "All these choices are correct"]}', NULL),
  ('E1D05', 5, '{"id": "E1D05", "correct": 3, "refs": "[97.213(d)]", "question": "What must be posted at the location of a station being operated by telecommand on or within 50 kilometers of the Earth\u2019s surface?", "answers": ["A photocopy of the station license", "A label with the name, address, and telephone number of the station licensee", "A label with the name, address, and telephone number of the control operator", "All these choices are correct"]}', NULL),
  ('E1D06', 5, '{"id": "E1D06", "correct": 0, "refs": "[97.215(c)]", "question": "What is the maximum permitted transmitter output power when operating a model craft by telecommand?", "answers": ["1 watt", "2 watts", "5 watts", "100 watts"]}', NULL),
  ('E1D07', 5, '{"id": "E1D07", "correct": 0, "refs": "[97.207]", "question": "Which of the following HF amateur bands include allocations for space stations?", "answers": ["40 meters, 20 meters, 15 meters, and 10 meters", "30 meters, 17 meters, and 10 meters", "Only 10 meters", "Satellite operation is permitted on all HF bands"]}', NULL),
  ('E1D08', 5, '{"id": "E1D08", "correct": 3, "refs": "[97.207]", "question": "Which VHF amateur bands have frequencies authorized for space stations?", "answers": ["6 meters and 2 meters", "6 meters, 2 meters, and 1.25 meters", "2 meters and 1.25 meters", "2 meters"]}', NULL),
  ('E1D09', 5, '{"id": "E1D09", "correct": 1, "refs": "[97.207]", "question": "Which UHF amateur bands have frequencies authorized for space stations?", "answers": ["70 centimeters only", "70 centimeters and 13 centimeters", "70 centimeters and 33 centimeters", "33 centimeters and 13 centimeters"]}', NULL),
  ('E1D10', 5, '{"id": "E1D10", "correct": 1, "refs": "[97.211]", "question": "Which amateur stations are eligible to be telecommand stations of space stations, subject to the privileges of the class of operator license held by the control operator of the station?", "answers": ["Any amateur station approved by AMSAT", "Any amateur station so designated by the space station licensee", "Any amateur station so designated by the ITU", "All these choices are correct"]}', NULL),
  ('E1D11', 5, '{"id": "E1D11", "correct": 3, "refs": "[97.209]", "question": "Which amateur stations are eligible to operate as Earth stations?", "answers": ["Any amateur licensee who has successfully completed the AMSAT space communications course", "Only those of General, Advanced or Amateur Extra class operators", "Only those of Amateur Extra class operators", "Any amateur station, subject to the privileges of the class of operator license held by the control operator"]}', NULL),
  ('E1D12', 5, '{"id": "E1D12", "correct": 0, "refs": "[97.207(e), 97.203(g)]", "question": "Which of the following amateur stations may transmit one-way communications?", "answers": ["A space station, beacon station, or telecommand station", "A local repeater or linked repeater station", "A message forwarding station or automatically controlled digital station", "All these choices are correct"]}', NULL),
  ('E1E01', 6, '{"id": "E1E01", "correct": 0, "refs": "[97.527]", "question": "For which types of out-of-pocket expenses do the Part 97 rules state that VEs and VECs may be reimbursed?", "answers": ["Preparing, processing, administering, and coordinating an examination for an amateur radio operator license", "Teaching an amateur operator license examination preparation course", "No expenses are authorized for reimbursement", "Providing amateur operator license examination preparation training materials"]}', NULL),
