CREATE TABLE license_class (
  id SERIAL PRIMARY KEY,
  code CHAR(1) NOT NULL UNIQUE,
  name VARCHAR(50) NOT NULL,
  description TEXT
);

CREATE TABLE subsection (
  id SERIAL PRIMARY KEY,
  code VARCHAR(3) NOT NULL UNIQUE,
  license_class_id INTEGER NOT NULL REFERENCES license_class(id)
);

CREATE TABLE question (
  id SERIAL PRIMARY KEY,
  code VARCHAR(5) NOT NULL UNIQUE,
  subsection_id INTEGER NOT NULL REFERENCES subsection(id),
  content JSONB NOT NULL,
  figure VARCHAR(255)
);

CREATE TABLE "user" (
  id SERIAL PRIMARY KEY,
  username VARCHAR(100) NOT NULL UNIQUE
);

CREATE TABLE user_question_mastery (
  id SERIAL PRIMARY KEY,
  user_id INTEGER NOT NULL REFERENCES "user"(id) ON DELETE CASCADE,
  question_id INTEGER NOT NULL REFERENCES question(id) ON DELETE CASCADE,
  mastery DECIMAL(5,2) NOT NULL DEFAULT 0.00,
  last_asked_time TIME,
  last_asked_date DATE,
  UNIQUE(user_id, question_id)
);

CREATE INDEX idx_subsection_license ON subsection(license_class_id);
CREATE INDEX idx_question_subsection ON question(subsection_id);
CREATE INDEX idx_uqm_user ON user_question_mastery(user_id);
CREATE INDEX idx_uqm_question ON user_question_mastery(question_id);

CREATE TABLE tool_calls (
  id SERIAL PRIMARY KEY,
  user_id INTEGER NOT NULL REFERENCES "user"(id) ON DELETE CASCADE,
  timestamp TIMESTAMPTZ NOT NULL DEFAULT NOW(),
  toolcall_description TEXT NOT NULL
);

CREATE INDEX idx_tool_calls_user ON tool_calls(user_id);




